import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import config from "../../config";
import "./ProductDetails.css";
import axios from "axios";
import Rating from '@mui/material/Rating';
import Typography from '@mui/material/Typography';

const ProductDetails = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [items, setItems] = useState([]);

  const fetchDetails = async () => {
    try {
      const response = await axios.get(`${config.endpoint}/${productId}`);
      const result = response.data;
      console.log(result);
      setItems(result);
    } catch (error) {
      console.log("error 3", error);
    }
  };

  useEffect(() => {
    fetchDetails();
  });

  const handleBackButton = () => {
    navigate("/");
  };

  return (
    <>
      <div onClick={() => handleBackButton()} className="back-page">
        Back
      </div>

      {
      items ? (
        items.map((item) => (
          <div className="products-container" key={item.id}>
            {/* <div>
              <img className="image-2" src={item.image} alt="product" />
            </div> */}

            {/* <div>
              <h2 className="details-container">Name - {item.title}</h2>
              <br />
              <h2 className="details-container">Category - {item.category}</h2>
              <br />
              <h2 className="details-container">Price - {item.price}</h2>
              <br />
              <h4 className="details-container">Details - {item.description}</h4>
              <br />
              <Rating name="read-only" value={item.rating.rate} readOnly />
            </div> */}
          </div>
        ))
      ) : (
        <h2>No Products Found</h2>
      )
       }
    </>
  )
}

export default ProductDetails;
