import React,{useState, useEffect} from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import "./Cart.css";

const Cart = ({ cart, setCart }) => {
    const [quantity, setQuantity] = useState(1);

    const handleAdd = () => {
        setQuantity((prevVal) => prevVal + 1)
    }

    const handleSubtract = () => {
        setQuantity((prevVal) => prevVal - 1)
    }
  return (
    <Box sx={{ width: "100%" }}>
      <Grid
        container
        spacing={2}
        className="grid-container"
        sx={{
          paddingLeft: "20px",
          paddingRight: "20px",
          paddingTop: "50px",
          paddingBottom: "50px",
        }}
      >
        {cart.map((product) => (
          <Grid item xs={12} sm={12} md={12} lg={12} key={product.id} className="cart-container">
            <Card sx={{ maxWidth: 345 }} >
              <CardMedia
                component="img"
                alt="green iguana"
                height="140"
                image={product.image}
                className="image"
              />
            </Card>
            <div className="details-container">
            <Typography gutterBottom variant="h5" component="div">
              {product.title}
            </Typography>
            <div className="price-container">
            <Typography gutterBottom variant="h5" component="div" onClick={()=>handleSubtract()}>
              {quantity >= 1 ? "-" : null}
            </Typography>
            <Typography gutterBottom variant="h5" component="div">
              {quantity}
            </Typography>
            <Typography gutterBottom variant="h5" component="div" onClick={()=>handleAdd()}>
              {quantity <= 10 ? "+" : null}
            </Typography>
            <Typography gutterBottom variant="h5" component="div">
              ${product.price}
            </Typography>
            </div>

            </div>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Cart;
