import React,{useState, useEffect} from 'react';
import config from '../../config';
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Rating from '@mui/material/Rating';

const ProductDetails = () => {
    const {productId} = useParams();
    const navigate = useNavigate();
    const [items, setItems] = useState([]);

    const fetchDetails = async() => {
            try {
                const response = await axios.get(`${config.endpoint}/${productId}`);
                const result = (response.data);
                let resArr = [];
                resArr.push(result)
                console.log(resArr);
                setItems(resArr);
            } catch (error){
                console.log('error', error)
            }
        }

        useEffect(()=>{
            fetchDetails();
        },[]);
    

    const handleBackButton = () => {
        navigate('/');
    }
  return (
    <>
      <div onClick={()=>handleBackButton()} className='back-page'>Back</div>

      {
        items ? (
            items.map((item, id) => (
                <div className="products-container" key={id}>
                    <div>
                        <img
                        className="image-2"
                        src={item.image}
                        alt="recipe" 
                        />
                    </div>
                    <div>
                        <h2 className="details-container">Name {" "} - {" "} {item.title}</h2>
                        <br />
                        <h2 className="details-container">Category {" "} - {" "}{item.category}</h2>
                        <br />
                        <h2 className="details-container">Price {" "} - {" "}${item.price}</h2>
                        <br />
                        <div className="details-container"><h2>Description {" "} - {" "}</h2><h3>{item.description}</h3></div>
                        <br />
                        <Rating name="read-only" value={item.rating.rate} readOnly />
                        <br />
                        <h2 className="details-container">Votes {" "} - {" "}{item.rating.count} upvotes</h2>

                    </div>
                  
                </div>
            ))
        ) : (
            <h2>No Product Found</h2>
        )
      }
    </>
  )
}

export default ProductDetails
