import React,{useState, useEffect} from 'react';
import './Ingredients.css';
import config from '../../config';
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Ingredients = () => {
    const {productId} = useParams();
    const navigate = useNavigate();
    const [items, setItems] = useState([]);

    const fetchDetails = async() => {
            try {
                const response = await axios.get(`${config.endpoint}/${productId}`);
                const result = response.data.meals;
                console.log(result);
                setItems(result);
            } catch (error){
                console.log('error', error)
            }
        }

        useEffect(()=>{
            fetchDetails();
        },[]);
    

    const handleBackButton = () => {
        navigate('/');
    }
  return (
    <>
      <div onClick={()=>handleBackButton()} className='back-page'>Back</div>

      {
        items ? (
            items.map((item, id) => (
                <div className="products-container" key={id}>
                    <div>
                        <img
                        className="image-2"
                        src={item.image}
                        alt="recipe" 
                        />
                    </div>
                    <div>
                        <h2 className="details-container">Name {" "} - {" "}{item.title}</h2>
                        <br />
                        <h2 className="details-container">Category {" "} - {" "}{item.category}</h2>
                        <br />
                        <br />
                        <h2 className="details-container">Price {" "} - {" "}${item.price}</h2>
                        <br />
                        <br />
                        <br />

                    </div>
                  
                </div>
            ))
        ) : (
            <h2>No Recipe Found</h2>
        )
      }
    </>
  )
}

export default Ingredients
